struct Particle {
    pos: vec2<f32>,
    oldPos: vec2<f32>,
    vel: vec2<f32>,
    invMass: f32,
    radius: f32,
};

struct DistanceConstraint {
    idxA: u32,
    idxB: u32,
    restLength: f32,
    compliance: f32,
};

struct BendingConstraint {
    idxA: u32,
    idxB: u32,
    idxC: u32,
    restLength: f32, // Distance between A and C
    compliance: f32,
    padding: u32,
};

struct Params {
    dt: f32,
    gravity: f32,
    numParticles: u32,
    numDistConstraints: u32,
    numBendConstraints: u32,
    substeps: u32,
    mousePos: vec2<f32>,
    activeParticleIdx: i32,
};

@group(0) @binding(0) var<storage, read_write> particles: array<Particle>;
@group(0) @binding(1) var<storage, read> distConstraints: array<DistanceConstraint>;
@group(0) @binding(2) var<storage, read> bendConstraints: array<BendingConstraint>;
@group(0) @binding(3) var<uniform> params: Params;

fn sdCircle(p: vec2<f32>, r: f32) -> f32 {
    return length(p) - r;
}

fn sdBox(p: vec2<f32>, b: vec2<f32>) -> f32 {
    let d = abs(p) - b;
    return length(max(d, vec2<f32>(0.0))) + min(max(d.x, d.y), 0.0);
}

@compute @workgroup_size(64)
fn integrate(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numParticles) { return; }

    var p = particles[i];
    if (p.invMass <= 0.0) {
        if (i == u32(params.activeParticleIdx)) {
             particles[i].pos = params.mousePos;
             particles[i].oldPos = params.mousePos;
        }
        return;
    }

    let h = params.dt / f32(params.substeps);
    let vel = (p.pos - p.oldPos) / h;
    let nextPos = p.pos + vel * h + vec2<f32>(0.0, params.gravity) * h * h;

    particles[i].oldPos = p.pos;
    particles[i].pos = nextPos;
}

@compute @workgroup_size(64)
fn solveDistance(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numDistConstraints) { return; }

    let c = distConstraints[i];
    let p1 = particles[c.idxA].pos;
    let p2 = particles[c.idxB].pos;
    let w1 = particles[c.idxA].invMass;
    let w2 = particles[c.idxB].invMass;
    let wSum = w1 + w2;
    if (wSum <= 0.0) { return; }

    let delta = p1 - p2;
    let dist = length(delta);
    if (dist < 0.0001) { return; }

    let h = params.dt / f32(params.substeps);
    let alpha = c.compliance / (h * h);
    let dLambda = -(dist - c.restLength) / (wSum + alpha);

    let correction = delta * (dLambda / dist);

    if (w1 > 0.0) { particles[c.idxA].pos += correction * w1; }
    if (w2 > 0.0) { particles[c.idxB].pos -= correction * w2; }
}

@compute @workgroup_size(64)
fn solveBending(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numBendConstraints) { return; }

    let c = bendConstraints[i];
    let p1 = particles[c.idxA].pos;
    let p2 = particles[c.idxC].pos; // Distance constraint between A and C for stiffness
    let w1 = particles[c.idxA].invMass;
    let w2 = particles[c.idxC].invMass;
    let wSum = w1 + w2;
    if (wSum <= 0.0) { return; }

    let delta = p1 - p2;
    let dist = length(delta);
    if (dist < 0.0001) { return; }

    let h = params.dt / f32(params.substeps);
    let alpha = c.compliance / (h * h);
    let dLambda = -(dist - c.restLength) / (wSum + alpha);
    let correction = delta * (dLambda / dist);

    if (w1 > 0.0) { particles[c.idxA].pos += correction * w1; }
    if (w2 > 0.0) { particles[c.idxC].pos -= correction * w2; }
}

@compute @workgroup_size(64)
fn solveCollisions(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numParticles) { return; }

    var p = particles[i];
    if (p.invMass <= 0.0) { return; }

    // World Boundary Collision with Radius
    let bounds = vec2<f32>(12.0, 7.0);
    let margin = p.radius;

    if (p.pos.x > bounds.x - margin) { p.pos.x = bounds.x - margin; }
    if (p.pos.x < -bounds.x + margin) { p.pos.x = -bounds.x + margin; }
    if (p.pos.y > bounds.y - margin) { p.pos.y = bounds.y - margin; }
    if (p.pos.y < -bounds.y + margin) { p.pos.y = -bounds.y + margin; }

    // Example Circle Obstacle Collision (SDF)
    let circlePos = vec2<f32>(4.0, 2.0);
    let circleRadius = 1.5;
    let distToCircle = length(p.pos - circlePos);
    if (distToCircle < circleRadius + p.radius) {
        let n = normalize(p.pos - circlePos);
        p.pos = circlePos + n * (circleRadius + p.radius);
    }

    particles[i] = p;
}

import * as THREE from 'three';

const MAX_PARTICLES = 4096;
const MAX_DIST_CONSTRAINTS = 4096;
const MAX_BEND_CONSTRAINTS = 4096;

export class WebPhysics {
    renderer: any;
    scene: THREE.Scene;
    bounds: { width: number; height: number };
    ready: boolean = false;

    particles = new Float32Array(MAX_PARTICLES * 8);
    distConstraints = new Uint32Array(MAX_DIST_CONSTRAINTS * 4);
    bendConstraints = new Uint32Array(MAX_BEND_CONSTRAINTS * 8);

    numParticles = 0;
    numDistConstraints = 0;
    numBendConstraints = 0;
    activeRope: any = null;

    device: GPUDevice | null = null;
    particleBuffer: GPUBuffer | null = null;
    distConstraintBuffer: GPUBuffer | null = null;
    bendConstraintBuffer: GPUBuffer | null = null;
    paramsBuffer: GPUBuffer | null = null;
    bindGroup: GPUBindGroup | null = null;

    pipelines: Record<string, GPUComputePipeline> = {};

    constructor(renderer: any, scene: THREE.Scene, bounds: { width: number; height: number }) {
        this.renderer = renderer;
        this.scene = scene;
        this.bounds = bounds;
    }

    async init(): Promise<void> {
        const device = this.renderer.device || this.renderer.backend?.device;
        if (!device) throw new Error('WebGPU device not found');
        this.device = device;

        const shaderCode = await (await fetch('/src/physics.wgsl')).text();
        const shaderModule = device.createShaderModule({ code: shaderCode });

        this.particleBuffer = device.createBuffer({
            size: this.particles.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST | GPUBufferUsage.COPY_SRC,
        });

        this.distConstraintBuffer = device.createBuffer({
            size: this.distConstraints.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST,
        });

        this.bendConstraintBuffer = device.createBuffer({
            size: this.bendConstraints.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST,
        });

        this.paramsBuffer = device.createBuffer({
            size: 64,
            usage: GPUBufferUsage.UNIFORM | GPUBufferUsage.COPY_DST,
        });

        const bgl = device.createBindGroupLayout({
            entries: [
                { binding: 0, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'storage' } },
                { binding: 1, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'read-only-storage' } },
                { binding: 2, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'read-only-storage' } },
                { binding: 3, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'uniform' } }
            ]
        });

        this.bindGroup = device.createBindGroup({
            layout: bgl,
            entries: [
                { binding: 0, resource: { buffer: this.particleBuffer } },
                { binding: 1, resource: { buffer: this.distConstraintBuffer } },
                { binding: 2, resource: { buffer: this.bendConstraintBuffer } },
                { binding: 3, resource: { buffer: this.paramsBuffer } }
            ]
        });

        const layout = device.createPipelineLayout({ bindGroupLayouts: [bgl] });
        const createPipe = (entry: string) => device.createComputePipeline({ layout, compute: { module: shaderModule, entryPoint: entry } });

        this.pipelines.integrate = createPipe('integrate');
        this.pipelines.solveDistance = createPipe('solveDistance');
        this.pipelines.solveBending = createPipe('solveBending');
        this.pipelines.solveCollisions = createPipe('solveCollisions');

        this.ready = true;
    }

    createRope(startPos: THREE.Vector2, segments = 15): any {
        const indices: number[] = [];
        const segLen = 0.5;

        for (let i = 0; i < segments; i++) {
            const idx = this.numParticles++;
            indices.push(idx);
            const pos = new THREE.Vector2(startPos.x, startPos.y - i * segLen);
            this.setParticle(idx, pos, i === 0 ? 0.0 : 1.0);
        }

        // Distance Constraints
        for (let i = 0; i < segments - 1; i++) {
            const cIdx = this.numDistConstraints++;
            this.setDistConstraint(cIdx, indices[i]!, indices[i+1]!, segLen, 0.0);
        }

        // XPBD Bending (Constraint between node i and i+2)
        for (let i = 0; i < segments - 2; i++) {
            const cIdx = this.numBendConstraints++;
            this.setBendConstraint(cIdx, indices[i]!, indices[i+1]!, indices[i+2]!, segLen * 2.0, 0.1);
        }

        const geo = new THREE.BufferGeometry();
        geo.setAttribute('position', new THREE.BufferAttribute(new Float32Array(segments * 3), 3));
        const mesh = new THREE.Line(geo, new THREE.LineBasicMaterial({ color: 0x00ffff }));
        this.scene.add(mesh);

        const rope = { indices, mesh, segments };
        this.activeRope = rope;
        this.syncGPU();
        return rope;
    }

    setParticle(i: number, pos: THREE.Vector2, invMass: number): void {
        const off = i * 8;
        this.particles[off] = pos.x;
        this.particles[off + 1] = pos.y;
        this.particles[off + 2] = pos.x;
        this.particles[off + 3] = pos.y;
        this.particles[off + 6] = invMass;
        this.particles[off + 7] = 0.1; // radius
    }

    setDistConstraint(i: number, a: number, b: number, len: number, compliance: number): void {
        const off = i * 4;
        this.distConstraints[off] = a;
        this.distConstraints[off + 1] = b;
        const fv = new Float32Array(this.distConstraints.buffer);
        fv[off + 2] = len;
        fv[off + 3] = compliance;
    }

    setBendConstraint(i: number, a: number, b: number, c: number, len: number, compliance: number): void {
        const off = i * 8;
        this.bendConstraints[off] = a;
        this.bendConstraints[off + 1] = b;
        this.bendConstraints[off + 2] = c;
        const fv = new Float32Array(this.bendConstraints.buffer);
        fv[off + 3] = len;
        fv[off + 4] = compliance;
    }

    syncGPU(): void {
        if (!this.device) return;
        this.device.queue.writeBuffer(this.particleBuffer!, 0, this.particles);
        this.device.queue.writeBuffer(this.distConstraintBuffer!, 0, this.distConstraints);
        this.device.queue.writeBuffer(this.bendConstraintBuffer!, 0, this.bendConstraints);
    }

    update(mousePos: THREE.Vector2): void {
        if (!this.ready || !this.device) return;

        const substeps = 20;
        const dt = 1.0 / 60.0;

        const params = new Float32Array(16);
        params[0] = dt; params[1] = -15.0; params[2] = this.numParticles; 
        params[3] = this.numDistConstraints; params[4] = this.numBendConstraints; 
        params[5] = substeps; params[6] = mousePos.x; params[7] = mousePos.y;
        params[8] = this.activeRope ? this.activeRope.indices[this.activeRope.segments - 1] : -1;

        this.device.queue.writeBuffer(this.paramsBuffer!, 0, params);

        const encoder = this.device.createCommandEncoder();
        for (let s = 0; s < substeps; s++) {
            const pass = encoder.beginComputePass();
            pass.setBindGroup(0, this.bindGroup!);
            pass.setPipeline(this.pipelines.integrate!);
            pass.dispatchWorkgroups(Math.ceil(MAX_PARTICLES / 64));
            pass.setPipeline(this.pipelines.solveDistance!);
            pass.dispatchWorkgroups(Math.ceil(MAX_DIST_CONSTRAINTS / 64));
            pass.setPipeline(this.pipelines.solveBending!);
            pass.dispatchWorkgroups(Math.ceil(MAX_BEND_CONSTRAINTS / 64));
            pass.setPipeline(this.pipelines.solveCollisions!);
            pass.dispatchWorkgroups(Math.ceil(MAX_PARTICLES / 64));
            pass.end();
        }
        this.device.queue.submit([encoder.finish()]);

        this.readBackAndVisualize();
    }

    async readBackAndVisualize() {
        if (!this.device || !this.activeRope) return;

        const readBuffer = this.device.createBuffer({ size: this.particles.byteLength, usage: GPUBufferUsage.COPY_DST | GPUBufferUsage.MAP_READ });
        const encoder = this.device.createCommandEncoder();
        encoder.copyBufferToBuffer(this.particleBuffer!, 0, readBuffer, 0, this.particles.byteLength);
        this.device.queue.submit([encoder.finish()]);

        await readBuffer.mapAsync(GPUMapMode.READ);
        const data = new Float32Array(readBuffer.getMappedRange());
        this.particles.set(data);
        readBuffer.unmap();
        readBuffer.destroy();

        const attr = this.activeRope.mesh.geometry.getAttribute('position');
        for (let i = 0; i < this.activeRope.segments; i++) {
            const idx = this.activeRope.indices[i];
            attr.setXYZ(i, this.particles[idx * 8]!, this.particles[idx * 8 + 1]!, 0);
        }
        attr.needsUpdate = true;
    }

    pinActiveRope(rope: any, pos: THREE.Vector2) {
        const lastIdx = rope.indices[rope.segments - 1];
        this.setParticle(lastIdx, pos, 0.0);
        this.activeRope = null;
        this.syncGPU();
    }

    adjustRopeLength(rope: any, delta: number) {
        // Real length adjustment for all segments could be complex, for now we don't scale existing nodes
    }
}

