struct Particle {
    pos: vec2<f32>,
    oldPos: vec2<f32>,
    vel: vec2<f32>,
    invMass: f32,
    radius: f32,
};

struct DistanceConstraint {
    idxA: u32,
    idxB: u32,
    restLength: f32,
    compliance: f32,
};

struct Params {
    dt: f32,
    gravity: f32,
    numParticles: u32,
    numDistConstraints: u32,
    substeps: u32,
    padding1: u32,
    mousePos: vec2<f32>,
    activeParticleIdx: i32,
    padding2: i32,
};

@group(0) @binding(0) var<storage, read_write> particles: array<Particle>;
@group(0) @binding(1) var<storage, read> distConstraints: array<DistanceConstraint>;
@group(0) @binding(2) var<uniform> params: Params;

@compute @workgroup_size(64)
fn integrate(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numParticles) { return; }

    var p = particles[i];
    if (p.invMass <= 0.0) {
        if (i == u32(params.activeParticleIdx)) {
             particles[i].pos = params.mousePos;
             particles[i].oldPos = params.mousePos;
        }
        return;
    }

    let h = params.dt / f32(params.substeps);
    let vel = (p.pos - p.oldPos) / h;
    let nextPos = p.pos + vel * h + vec2<f32>(0.0, params.gravity) * h * h;

    particles[i].oldPos = p.pos;
    particles[i].pos = nextPos;
}

@compute @workgroup_size(64)
fn solveDistance(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numDistConstraints) { return; }

    let c = distConstraints[i];
    let p1 = particles[c.idxA].pos;
    let p2 = particles[c.idxB].pos;
    let w1 = particles[c.idxA].invMass;
    let w2 = particles[c.idxB].invMass;
    let wSum = w1 + w2;

    if (wSum <= 0.0) { return; }

    let delta = p1 - p2;
    let dist = length(delta);
    if (dist < 0.0001) { return; }

    let h = params.dt / f32(params.substeps);
    let alpha = c.compliance / (h * h);
    let dLambda = -(dist - c.restLength) / (wSum + alpha);
    let correction = delta * (dLambda / dist);

    // XPBD - apply immediate correction (Jacobi-style approximation on GPU)
    if (w1 > 0.0) { particles[c.idxA].pos += correction * w1 * 0.5; }
    if (w2 > 0.0) { particles[c.idxB].pos -= correction * w2 * 0.5; }
}

@compute @workgroup_size(64)
fn solveCollisions(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numParticles) { return; }

    var p = particles[i];
    if (p.invMass <= 0.0) { return; }

    let bounds = vec2<f32>(11.5, 6.5);
    if (p.pos.x > bounds.x) { p.pos.x = bounds.x; }
    if (p.pos.x < -bounds.x) { p.pos.x = -bounds.x; }
    if (p.pos.y > bounds.y) { p.pos.y = bounds.y; }
    if (p.pos.y < -bounds.y) { p.pos.y = -bounds.y; }

    particles[i].pos = p.pos;
}

import * as THREE from 'three';

const MAX_PARTICLES = 2048;
const MAX_CONSTRAINTS = 2048;

export class WebPhysics {
    renderer: any;
    scene: THREE.Scene;
    bounds: { width: number; height: number };
    ready: boolean = false;

    // Float32Array where each particle is 8 floats (32 bytes)
    particles = new Float32Array(MAX_PARTICLES * 8);
    // Uint32Array where each constraint is 4 uints/floats (16 bytes)
    distConstraints = new Uint32Array(MAX_CONSTRAINTS * 4);

    numParticles = 0;
    numDistConstraints = 0;
    activeRope: any = null;

    device: GPUDevice | null = null;
    particleBuffer: GPUBuffer | null = null;
    distConstraintBuffer: GPUBuffer | null = null;
    paramsBuffer: GPUBuffer | null = null;
    stagingBuffer: GPUBuffer | null = null;
    bindGroup: GPUBindGroup | null = null;

    pipelines: Record<string, GPUComputePipeline> = {};
    isReadingBack = false;

    constructor(renderer: any, scene: THREE.Scene, bounds: { width: number; height: number }) {
        this.renderer = renderer;
        this.scene = scene;
        this.bounds = bounds;
    }

    async init(): Promise<void> {
        const device = this.renderer.device || this.renderer.backend?.device;
        if (!device) throw new Error('WebGPU device not found');
        this.device = device;

        const shaderCode = await (await fetch('/src/physics.wgsl')).text();
        const shaderModule = device.createShaderModule({ code: shaderCode });

        this.particleBuffer = device.createBuffer({
            size: this.particles.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST | GPUBufferUsage.COPY_SRC,
        });

        this.distConstraintBuffer = device.createBuffer({
            size: this.distConstraints.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST,
        });

        this.paramsBuffer = device.createBuffer({
            size: 64, // Struct Params is 48-64 bytes with padding
            usage: GPUBufferUsage.UNIFORM | GPUBufferUsage.COPY_DST,
        });

        this.stagingBuffer = device.createBuffer({
            size: this.particles.byteLength,
            usage: GPUBufferUsage.MAP_READ | GPUBufferUsage.COPY_DST,
        });

        const bgl = device.createBindGroupLayout({
            entries: [
                { binding: 0, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'storage' } },
                { binding: 1, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'read-only-storage' } },
                { binding: 2, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'uniform' } }
            ]
        });

        this.bindGroup = device.createBindGroup({
            layout: bgl,
            entries: [
                { binding: 0, resource: { buffer: this.particleBuffer } },
                { binding: 1, resource: { buffer: this.distConstraintBuffer } },
                { binding: 2, resource: { buffer: this.paramsBuffer } }
            ]
        });

        const layout = device.createPipelineLayout({ bindGroupLayouts: [bgl] });
        const createPipe = (entry: string) => device.createComputePipeline({ layout, compute: { module: shaderModule, entryPoint: entry } });

        this.pipelines.integrate = createPipe('integrate');
        this.pipelines.solveDistance = createPipe('solveDistance');
        this.pipelines.solveCollisions = createPipe('solveCollisions');

        this.ready = true;
    }

    createRope(startPos: THREE.Vector2): any {
        const segments = 20;
        const indices: number[] = [];
        const segLen = 0.4;

        for (let i = 0; i < segments; i++) {
            const idx = this.numParticles++;
            indices.push(idx);
            const pos = new THREE.Vector2(startPos.x, startPos.y - i * segLen);
            this.setParticle(idx, pos, i === 0 ? 0.0 : 1.0);
        }

        for (let i = 0; i < segments - 1; i++) {
            const cIdx = this.numDistConstraints++;
            this.setDistConstraint(cIdx, indices[i]!, indices[i+1]!, segLen, 0.000001);
        }

        const geo = new THREE.BufferGeometry();
        geo.setAttribute('position', new THREE.BufferAttribute(new Float32Array(segments * 3), 3));
        const mesh = new THREE.Line(geo, new THREE.LineBasicMaterial({ color: 0x00ffff }));
        this.scene.add(mesh);

        const rope = { indices, mesh, segments };
        this.activeRope = rope;
        this.syncGPU();
        return rope;
    }

    setParticle(i: number, pos: THREE.Vector2, invMass: number): void {
        const off = i * 8;
        this.particles[off] = pos.x;
        this.particles[off + 1] = pos.y;
        this.particles[off + 2] = pos.x; // oldX
        this.particles[off + 3] = pos.y; // oldY
        this.particles[off + 6] = invMass;
        this.particles[off + 7] = 0.1; // radius
    }

    setDistConstraint(i: number, a: number, b: number, len: number, compliance: number): void {
        const off = i * 4;
        this.distConstraints[off] = a;
        this.distConstraints[off + 1] = b;
        const fv = new Float32Array(this.distConstraints.buffer);
        fv[off + 2] = len;
        fv[off + 3] = compliance;
    }

    syncGPU(): void {
        if (!this.device) return;
        this.device.queue.writeBuffer(this.particleBuffer!, 0, this.particles);
        this.device.queue.writeBuffer(this.distConstraintBuffer!, 0, this.distConstraints);
    }

    update(mousePos: THREE.Vector2): void {
        if (!this.ready || !this.device || this.isReadingBack) return;

        const substeps = 10;
        const dt = 1.0 / 60.0;

        // Params Layout Alignment: dt, gravity, numP, numC, subs, padding, mouseX, mouseY, activeIdx, padding
        const params = new Float32Array(16);
        params[0] = dt; 
        params[1] = -12.0; 
        params[2] = this.numParticles;
        params[3] = this.numDistConstraints;
        params[4] = substeps;
        params[6] = mousePos.x;
        params[7] = mousePos.y;
        params[8] = this.activeRope ? this.activeRope.indices[this.activeRope.segments - 1] : -1;

        this.device.queue.writeBuffer(this.paramsBuffer!, 0, params);

        const encoder = this.device.createCommandEncoder();
        for (let s = 0; s < substeps; s++) {
            const pass = encoder.beginComputePass();
            pass.setBindGroup(0, this.bindGroup!);
            pass.setPipeline(this.pipelines.integrate!);
            pass.dispatchWorkgroups(Math.ceil(MAX_PARTICLES / 64));
            pass.setPipeline(this.pipelines.solveDistance!);
            pass.dispatchWorkgroups(Math.ceil(MAX_CONSTRAINTS / 64));
            pass.setPipeline(this.pipelines.solveCollisions!);
            pass.dispatchWorkgroups(Math.ceil(MAX_PARTICLES / 64));
            pass.end();
        }

