import * as THREE from 'three';
// @ts-ignore - WebGPU is built-in to three.js r182+
import WebGPURenderer from 'three/src/renderers/webgpu/WebGPURenderer.js';
import { WebPhysics } from './webPhysics';

// WebGPU global types
declare global {
  interface Navigator {
    gpu?: GPU;
  }
}

let scene: THREE.Scene, camera: THREE.OrthographicCamera, renderer: any, physics: WebPhysics, stateDisplay: HTMLSpanElement;
let activeRope: any = null;
const mouseWorld = new THREE.Vector2();
const BOUNDS = { width: 24, height: 14 };

// WebGPU Detection
async function checkWebGPU(): Promise<boolean> {
    if (!navigator.gpu) {
        const warning = document.getElementById('warning');
        if (warning) warning.classList.add('show');
        console.error('WebGPU nie dostępna w tej przeglądarce');
        return false;
    }
    return true;
}

async function init() {
    // Check WebGPU support
    if (!(await checkWebGPU())) return;

    stateDisplay = document.getElementById('state') as HTMLSpanElement;
    scene = new THREE.Scene();
    camera = new THREE.OrthographicCamera(-BOUNDS.width/2, BOUNDS.width/2, BOUNDS.height/2, -BOUNDS.height/2, 0.1, 1000);
    camera.position.z = 10;

    renderer = new WebGPURenderer({ antialias: true });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Wait for WebGPU renderer to be ready
    await renderer.init();

    physics = new WebPhysics(renderer, scene, BOUNDS);
    await physics.init();

    const arenaGeo = new THREE.EdgesGeometry(new THREE.BoxGeometry(BOUNDS.width, BOUNDS.height, 0));
    scene.add(new THREE.LineSegments(arenaGeo, new THREE.LineBasicMaterial({ color: 0x444444 })));

    window.addEventListener('mousedown', onMouseDown);
    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('wheel', onWheel);
    window.addEventListener('resize', () => renderer.setSize(window.innerWidth, window.innerHeight));

    animate();
}

function getMouseWorld(e: MouseEvent): THREE.Vector2 {
    const x = (e.clientX / window.innerWidth) * 2 - 1;
    const y = -(e.clientY / window.innerHeight) * 2 + 1;
    return new THREE.Vector2(x * (BOUNDS.width / 2), y * (BOUNDS.height / 2));
}

function onMouseDown(e: MouseEvent) {
    if (!physics.ready) return;
    const pos = getMouseWorld(e);

    if (activeRope) {
        // Pin to wall or just release
        physics.pinActiveRope(activeRope, pos);
        activeRope = null;
    } else {
        activeRope = physics.createRope(pos);
    }
}

function onMouseMove(e: MouseEvent) {
    mouseWorld.copy(getMouseWorld(e));
}

function onWheel(e: WheelEvent) {
    if (activeRope) {
        const delta = e.deltaY * 0.01;
        physics.adjustRopeLength(activeRope, delta);
    }
}

function animate() {
    requestAnimationFrame(animate);
    if (physics.ready) {
        physics.update(mouseWorld);
    }
    renderer.render(scene, camera);

    stateDisplay.innerText = `XPBD WebGPU | Active: ${activeRope ? 'YES (Scroll to reel)' : 'NO (Click to start)'}`;
}

init();

struct Particle {
    pos: vec2<f32>,
    oldPos: vec2<f32>,
    vel: vec2<f32>,
    invMass: f32,
    radius: f32,
};

struct Constraint {
    idxA: u32,
    idxB: u32,
    restLength: f32,
    compliance: f32,
    is_active: u32,
    padding: vec3<u32>,
};

struct Params {
    dt: f32,
    gravity: f32,
    numParticles: u32,
    numConstraints: u32,
    mousePos: vec2<f32>,
    activeParticleIdx: i32,
    substeps: u32,
};

@group(0) @binding(0) var<storage, read_write> particles: array<Particle>;
@group(0) @binding(1) var<storage, read_write> constraints: array<Constraint>;
@group(0) @binding(2) var<uniform> params: Params;

fn sdBox(p: vec2<f32>, b: vec2<f32>) -> f32 {
    let d = abs(p) - b;
    return length(max(d, vec2<f32>(0.0))) + min(max(d.x, d.y), 0.0);
}

@compute @workgroup_size(64)
fn integrate(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numParticles) { return; }

    var p = particles[i];
    if (p.invMass <= 0.0) {
        if (i == u32(params.activeParticleIdx)) {
             particles[i].pos = params.mousePos;
             particles[i].oldPos = params.mousePos;
        }
        return;
    }

    let h = params.dt / f32(params.substeps);
    let vel = (p.pos - p.oldPos) / h;
    let nextPos = p.pos + vel * h + vec2<f32>(0.0, params.gravity) * h * h;

    particles[i].oldPos = p.pos;
    particles[i].pos = nextPos;
}

@compute @workgroup_size(64)
fn solveDistance(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numConstraints) { return; }

    let c = constraints[i];
    if (c.is_active == 0u) { return; }

    let p1 = particles[c.idxA].pos;
    let p2 = particles[c.idxB].pos;
    let w1 = particles[c.idxA].invMass;
    let w2 = particles[c.idxB].invMass;
    let wSum = w1 + w2;
    if (wSum <= 0.0) { return; }

    let delta = p1 - p2;
    let dist = length(delta);
    if (dist < 0.0001) { return; }

    let h = params.dt / f32(params.substeps);
    let alpha = c.compliance / (h * h);
    let dLambda = -(dist - c.restLength) / (wSum + alpha);

    let correction = delta * (dLambda / dist);

    if (w1 > 0.0) { particles[c.idxA].pos += correction * w1; }
    if (w2 > 0.0) { particles[c.idxB].pos -= correction * w2; }
}

@compute @workgroup_size(64)
fn solveCollisions(@builtin(global_invocation_id) id: vec3<u32>) {
    let i = id.x;
    if (i >= params.numParticles) { return; }

    var p = particles[i];
    if (p.invMass <= 0.0) { return; }

    // Arena collision (Box SDF inverse)
    let bounds = vec2<f32>(11.8, 6.8); // Slightly inside BOUNDS
    let d = sdBox(p.pos, bounds);
    if (d > 0.0) {
        // Simplified normal: find closest edge
        if (abs(p.pos.x) > bounds.x) { p.pos.x = sign(p.pos.x) * bounds.x; }
        if (abs(p.pos.y) > bounds.y) { p.pos.y = sign(p.pos.y) * bounds.y; }
    }

    particles[i] = p;
}

import * as THREE from 'three';

const MAX_PARTICLES = 2048;
const MAX_CONSTRAINTS = 2048;

export class WebPhysics {
    renderer: any;
    scene: THREE.Scene;
    bounds: { width: number; height: number };
    ready: boolean;
    particles: Float32Array;
    constraints: Uint32Array;
    numParticles: number;
    numConstraints: number;
    activeRope: any;
    device: any;
    particleBuffer: any;
    constraintBuffer: any;
    paramsBuffer: any;
    bindGroup: any;
    integratePipeline: any;
    solveDistancePipeline: any;
    solveCollisionsPipeline: any;

    constructor(renderer: any, scene: THREE.Scene, bounds: { width: number; height: number }) {
        this.renderer = renderer;
        this.scene = scene;
        this.bounds = bounds;
        this.ready = false;

        this.particles = new Float32Array(MAX_PARTICLES * 8);
        this.constraints = new Uint32Array(MAX_CONSTRAINTS * 8);

        this.numParticles = 0;
        this.numConstraints = 0;
        this.activeRope = null;
        this.device = null;
        this.particleBuffer = null;
        this.constraintBuffer = null;
        this.paramsBuffer = null;
        this.bindGroup = null;
        this.integratePipeline = null;
        this.solveDistancePipeline = null;
        this.solveCollisionsPipeline = null;
    }

    async init(): Promise<void> {
        // WebGPU device - three.js r182+ exposes it directly
        const device = this.renderer.device || this.renderer.backend?.device;

        if (!device) {
            console.error('WebGPU device not available');
            return;
        }

        this.device = device;

        const shaderModule = device.createShaderModule({
            code: await (await fetch('/src/physics.wgsl')).text()
        });

        this.particleBuffer = device.createBuffer({
            size: this.particles.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST | GPUBufferUsage.VERTEX,
        });

        this.constraintBuffer = device.createBuffer({
            size: this.constraints.byteLength,
            usage: GPUBufferUsage.STORAGE | GPUBufferUsage.COPY_DST,
        });

        this.paramsBuffer = device.createBuffer({
            size: 64,
            usage: GPUBufferUsage.UNIFORM | GPUBufferUsage.COPY_DST,
        });

        const bindGroupLayout = device.createBindGroupLayout({
            entries: [
                { binding: 0, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'storage' } },
                { binding: 1, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'storage' } },
                { binding: 2, visibility: GPUShaderStage.COMPUTE, buffer: { type: 'uniform' } }
            ]
        });

        this.bindGroup = device.createBindGroup({
            layout: bindGroupLayout,
            entries: [
                { binding: 0, resource: { buffer: this.particleBuffer } },
                { binding: 1, resource: { buffer: this.constraintBuffer } },
                { binding: 2, resource: { buffer: this.paramsBuffer } }
            ]
        });

        const pipelineLayout = device.createPipelineLayout({ bindGroupLayouts: [bindGroupLayout] });

        this.integratePipeline = device.createComputePipeline({ layout: pipelineLayout, compute: { module: shaderModule, entryPoint: 'integrate' } });
        this.solveDistancePipeline = device.createComputePipeline({ layout: pipelineLayout, compute: { module: shaderModule, entryPoint: 'solveDistance' } });
        this.solveCollisionsPipeline = device.createComputePipeline({ layout: pipelineLayout, compute: { module: shaderModule, entryPoint: 'solveCollisions' } });

        this.ready = true;
    }

    createRope(startPos: THREE.Vector2): any {
        const startIdx = this.numParticles++;
        const endIdx = this.numParticles++;

        this.setParticle(startIdx, startPos, 0.0);
        this.setParticle(endIdx, startPos, 1.0);

        const cIdx = this.numConstraints++;
        this.setConstraint(cIdx, startIdx, endIdx, 0.5, 0.00001);

        const geo = new THREE.BufferGeometry();
        const mesh = new THREE.Line(geo, new THREE.LineBasicMaterial({ color: 0xffffff }));
        this.scene.add(mesh);

        const rope = { startIdx, endIdx, cIdx, mesh, length: 0.5 };
        this.activeRope = rope;
        return rope;
    }

    pinActiveRope(rope: any, pos: THREE.Vector2): void {
        this.setParticle(rope.endIdx, pos, 0.0);
        this.activeRope = null;
        this.syncGPU();
    }

    adjustRopeLength(rope: any, delta: number): void {
        rope.length = Math.max(0.1, rope.length + delta);
        this.setConstraint(rope.cIdx, rope.startIdx, rope.endIdx, rope.length, 0.00001);
        this.syncGPU();
    }

    setParticle(i: number, pos: THREE.Vector2, invMass: number): void {
        const off = i * 8;
        this.particles[off] = pos.x;
        this.particles[off + 1] = pos.y;
        this.particles[off + 2] = pos.x;
        this.particles[off + 3] = pos.y;
        this.particles[off + 6] = invMass;
        this.particles[off + 7] = 0.05;
    }

    setConstraint(i: number, a: number, b: number, len: number, compliance: number): void {
        const off = i * 8;
        this.constraints[off] = a;
        this.constraints[off + 1] = b;
        const floatView = new Float32Array(this.constraints.buffer);
        floatView[off + 2] = len;
        floatView[off + 3] = compliance;
        this.constraints[off + 4] = 1;
    }

    syncGPU(): void {
        if (!this.device || !this.particleBuffer || !this.constraintBuffer) return;
        this.device.queue.writeBuffer(this.particleBuffer, 0, this.particles);
        this.device.queue.writeBuffer(this.constraintBuffer, 0, this.constraints);
    }

    update(mousePos: THREE.Vector2): void {
        if (!this.ready || !this.device || !this.paramsBuffer) return;

        const substeps = 15;
        const dt = 1.0 / 60.0;

        const params = new Float32Array(16);
        params[0] = dt;
        params[1] = -9.8;
        params[2] = this.numParticles;
        params[3] = this.numConstraints;
        params[4] = mousePos.x;
        params[5] = mousePos.y;
        params[6] = this.activeRope ? this.activeRope.endIdx : -1;
        params[7] = substeps;
        this.device.queue.writeBuffer(this.paramsBuffer, 0, params);

        const commandEncoder = this.device.createCommandEncoder();

        for(let s = 0; s < substeps; s++) {
            const pass = commandEncoder.beginComputePass();
            pass.setBindGroup(0, this.bindGroup!);

            pass.setPipeline(this.integratePipeline!);
            pass.dispatchWorkgroups(Math.ceil(MAX_PARTICLES / 64));

            pass.setPipeline(this.solveDistancePipeline!);
            pass.dispatchWorkgroups(Math.ceil(MAX_CONSTRAINTS / 64));

            pass.setPipeline(this.solveCollisionsPipeline!);
            pass.dispatchWorkgroups(Math.ceil(MAX_PARTICLES / 64));

            pass.end();
        }

        const readBuffer = this.device.createBuffer({size: this.particles.byteLength, usage: GPUBufferUsage.MAP_READ | GPUBufferUsage.COPY_DST});
        commandEncoder.copyBufferToBuffer(this.particleBuffer!, 0, readBuffer, 0, this.particles.byteLength);

        this.device.queue.submit([commandEncoder.finish()]);
        this.readBackAndVisualize();
    }

    async readBackAndVisualize(): Promise<void> {
        if (!this.device) return;

        const readBuffer = this.device.createBuffer({
            size: this.particles.byteLength,
            usage: GPUBufferUsage.MAP_READ | GPUBufferUsage.COPY_DST
        });

        const commandEncoder = this.device.createCommandEncoder();
        commandEncoder.copyBufferToBuffer(this.particleBuffer!, 0, readBuffer, 0, this.particles.byteLength);
        this.device.queue.submit([commandEncoder.finish()]);

        await readBuffer.mapAsync(GPUMapMode.READ);
        const arrayBuffer = readBuffer.getMappedRange();
        this.particles.set(new Float32Array(arrayBuffer));
        readBuffer.unmap();

        if (this.activeRope) {
            const p1 = this.activeRope.startIdx * 8;
            const p2 = this.activeRope.endIdx * 8;
            const pts = new Float32Array([
                this.particles[p1]!, this.particles[p1+1]!, 0,
                this.particles[p2]!, this.particles[p2+1]!, 0
            ]);
            this.activeRope.mesh.geometry.setAttribute('position', new THREE.BufferAttribute(pts, 3));
        }
    }
}

